CREATE TABLE `test`.`users` (`id` INT NOT NULL AUTO_INCREMENT , `username` VARCHAR(255) NOT NULL , `email` VARCHAR(255) NOT NULL , `password` VARCHAR(255) NOT NULL , `created_at` DATE NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;

CREATE TABLE `test`.`tasks` (`id` INT NOT NULL AUTO_INCREMENT , `user_id` INT NOT NULL , `tiltle` VARCHAR(255) NOT NULL , `description` TEXT NOT NULL , `due_date` DATE NULL , `is_done` BIT NOT NULL DEFAULT b'0' , `created_at` DATE NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB; 